import React, { createContext, useContext, useState, useEffect } from 'react';

const UserContext = createContext();

export const useUser = () => {
  const context = useContext(UserContext);
  if (!context) {
    throw new Error('useUser must be used within UserProvider');
  }
  return context;
};

export const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState({
    genres: [],
    authors: [],
    readingGoal: 12
  });

  // Load user data from localStorage on mount
  useEffect(() => {
    const savedUser = localStorage.getItem('bookApp_user');
    const savedPreferences = localStorage.getItem('bookApp_preferences');
    
    if (savedUser) setUser(JSON.parse(savedUser));
    if (savedPreferences) setPreferences(JSON.parse(savedPreferences));
  }, []);

  // Save to localStorage whenever user or preferences change
  useEffect(() => {
    if (user) localStorage.setItem('bookApp_user', JSON.stringify(user));
  }, [user]);

  useEffect(() => {
    localStorage.setItem('bookApp_preferences', JSON.stringify(preferences));
  }, [preferences]);

  const value = {
    user,
    setUser,
    preferences,
    setPreferences
  };

  return (
    <UserContext.Provider value={value}>
      {children}
    </UserContext.Provider>
  );
};
