import React, { useState, useEffect } from 'react';
import { useBooks } from '../../contexts/BookContext';
import SearchBar from '../../components/SearchBar';
import RecommendationList from '../../components/RecommendationList';
import { getPopularBooks } from '../../services/bookAPI';
import './Home.css';

const Home = () => {
  const { books, setBooks, loading, setLoading } = useBooks();

  useEffect(() => {
    const loadPopularBooks = async () => {
      setLoading(true);
      const popularBooks = await getPopularBooks(10);
      setBooks(popularBooks);
      setLoading(false);
    };

    loadPopularBooks();
  }, [setBooks, setLoading]);

  return (
    <div className="home">
      <section className="hero">
        <h2>Discover Your Next Great Read</h2>
        <SearchBar />
      </section>
      
      <section className="popular-books">
        <h3>Popular Books</h3>
        {loading ? (
          <p>Loading books...</p>
        ) : (
          <RecommendationList books={books} title="Popular Now" />
        )}
      </section>
    </div>
  );
};

export default Home;
