import React, { useState, useEffect } from 'react';
import { useBooks } from '../../contexts/BookContext';
import SearchBar from '../../components/SearchBar';
import BookCard from '../../components/BookCard';
import { getPopularBooks } from '../../services/bookAPI';
import { TrendingUp, Search } from 'lucide-react';
import './Home.css';

const Home = () => {
  const { searchResults, loading, setLoading, setError, error } = useBooks();
  const [popularBooks, setPopularBooks] = useState([]);

  useEffect(() => {
    const loadPopularBooks = async () => {
      try {
        setLoading(true);
        const books = await getPopularBooks(12);
        setPopularBooks(books);
      } catch (error) {
        setError('Failed to load popular books');
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    loadPopularBooks();
  }, [setLoading, setError]);

  return (
    <div className="home">
      <section className="hero">
        <h2>Discover Your Next Great Read</h2>
        <p>Search for books or explore our recommendations</p>
        <SearchBar />
      </section>

      {error && (
        <div className="error-message">
          <p>{error}</p>
          <button onClick={() => setError(null)}>Dismiss</button>
        </div>
      )}
      
      {searchResults.length > 0 && (
        <section className="search-results">
          <h3>
            <Search className="section-icon" size={24} />
            Search Results ({searchResults.length} found)
          </h3>
          <div className="books-grid">
            {searchResults.map((book) => (
              <BookCard key={book.id} book={book} />
            ))}
          </div>
        </section>
      )}
      
      <section className="popular-books">
        <h3>
          <TrendingUp className="section-icon" size={24} />
          Popular Books
        </h3>
        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
            <p>Loading amazing books...</p>
          </div>
        ) : popularBooks.length > 0 ? (
          <div className="books-grid">
            {popularBooks.slice(0, 8).map((book) => (
              <BookCard key={book.id} book={book} />
            ))}
          </div>
        ) : (
          <p>No books available</p>
        )}
      </section>
    </div>
  );
};

export default Home;
