// Recommendation algorithms
export const generateRecommendations = (userRatings, userPreferences, allBooks, readingList) => {
  const readingListIds = readingList.map(book => book.id);
  const availableBooks = allBooks.filter(book => !readingListIds.includes(book.id));
  
  if (availableBooks.length === 0) return [];

  // Get user's highly rated books (4+ stars)
  const highlyRatedBooks = Object.entries(userRatings)
    .filter(([_, rating]) => rating >= 4)
    .map(([bookId]) => bookId);

  const recommendationScores = availableBooks.map(book => {
    let score = 0;
    
    // Score based on genres from highly rated books
    const userGenres = getUserGenresFromRatings(userRatings, allBooks);
    if (book.categories) {
      book.categories.forEach(category => {
        if (userGenres.includes(category.toLowerCase())) {
          score += 3;
        }
      });
    }

    // Score based on average rating
    if (book.averageRating >= 4) score += 2;
    if (book.averageRating >= 4.5) score += 1;

    // Score based on user preferences
    if (userPreferences.genres) {
      userPreferences.genres.forEach(genre => {
        if (book.categories && book.categories.some(cat => 
          cat.toLowerCase().includes(genre.toLowerCase())
        )) {
          score += 2;
        }
      });
    }

    // Score based on similar authors
    if (userPreferences.authors) {
      userPreferences.authors.forEach(author => {
        if (book.authors.some(bookAuthor => 
          bookAuthor.toLowerCase().includes(author.toLowerCase())
        )) {
          score += 4;
        }
      });
    }

    // Boost recent books
    if (book.publishedDate) {
      const year = parseInt(book.publishedDate.substring(0, 4));
      if (year >= 2020) score += 1;
      if (year >= 2022) score += 1;
    }

    return { ...book, recommendationScore: score };
  });

  return recommendationScores
    .filter(book => book.recommendationScore > 0)
    .sort((a, b) => b.recommendationScore - a.recommendationScore)
    .slice(0, 20);
};

const getUserGenresFromRatings = (userRatings, allBooks) => {
  const genres = [];
  Object.entries(userRatings).forEach(([bookId, rating]) => {
    if (rating >= 4) {
      const book = allBooks.find(b => b.id === bookId);
      if (book && book.categories) {
        genres.push(...book.categories.map(cat => cat.toLowerCase()));
      }
    }
  });
  return [...new Set(genres)];
};

export const getRecommendationReason = (book, userRatings, userPreferences, allBooks) => {
  const reasons = [];
  
  // Check genre matches
  const userGenres = getUserGenresFromRatings(userRatings, allBooks);
  if (book.categories) {
    const matchingGenres = book.categories.filter(cat => 
      userGenres.includes(cat.toLowerCase())
    );
    if (matchingGenres.length > 0) {
      reasons.push(`You enjoyed ${matchingGenres[0]} books`);
    }
  }

  // Check author preferences
  if (userPreferences.authors) {
    const matchingAuthor = userPreferences.authors.find(author =>
      book.authors.some(bookAuthor => 
        bookAuthor.toLowerCase().includes(author.toLowerCase())
      )
    );
    if (matchingAuthor) {
      reasons.push(`You like ${matchingAuthor}`);
    }
  }

  // Check high rating - removed emoji
  if (book.averageRating >= 4.5) {
    reasons.push(`Highly rated (${book.averageRating}/5)`);
  }

  return reasons.length > 0 ? reasons[0] : 'Recommended for you';
};

export const formatBookForRecommendation = (book, reason) => ({
  ...book,
  recommendationReason: reason
});

// Genre options for user preferences
export const GENRE_OPTIONS = [
  'Fiction',
  'Non-fiction',
  'Mystery',
  'Romance',
  'Science Fiction',
  'Fantasy',
  'Biography',
  'History',
  'Self-help',
  'Business',
  'Psychology',
  'Philosophy',
  'Science',
  'Technology',
  'Health',
  'Cooking',
  'Travel',
  'Art',
  'Poetry',
  'Drama'
];

// Reading status configuration
export const READING_STATUS_OPTIONS = [
  {
    value: 'want-to-read',
    label: 'Want to Read',
    color: '#ffc107'
  },
  {
    value: 'currently-reading',
    label: 'Currently Reading',
    color: '#28a745'
  },
  {
    value: 'finished',
    label: 'Finished',
    color: '#007bff'
  }
];

// Utility functions
export const formatDate = (dateString) => {
  if (!dateString) return 'Unknown';
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });
};

export const truncateText = (text, maxLength = 150) => {
  if (!text) return 'No description available';
  const cleanText = text.replace(/<[^>]*>/g, '');
  return cleanText.length > maxLength ? 
    cleanText.substring(0, maxLength) + '...' : 
    cleanText;
};

export const getBookImage = (book) => {
  if (book.thumbnail) return book.thumbnail;
  if (book.imageLinks?.thumbnail) return book.imageLinks.thumbnail;
  if (book.imageLinks?.smallThumbnail) return book.imageLinks.smallThumbnail;
  return null;
};

export const normalizeBookData = (apiBook) => {
  const volumeInfo = apiBook.volumeInfo || {};
  
  return {
    id: apiBook.id,
    title: volumeInfo.title || 'Unknown Title',
    authors: volumeInfo.authors || ['Unknown Author'],
    description: volumeInfo.description || '',
    publishedDate: volumeInfo.publishedDate || '',
    pageCount: volumeInfo.pageCount || 0,
    categories: volumeInfo.categories || [],
    averageRating: volumeInfo.averageRating || 0,
    ratingsCount: volumeInfo.ratingsCount || 0,
    thumbnail: getBookImage(volumeInfo),
    previewLink: volumeInfo.previewLink || '',
    infoLink: volumeInfo.infoLink || '',
    isbn: volumeInfo.industryIdentifiers?.[0]?.identifier || ''
  };
};

// Book collection management
export const sortBooksByRelevance = (books, query) => {
  if (!query) return books;
  
  const queryLower = query.toLowerCase();
  
  return books.sort((a, b) => {
    let scoreA = 0;
    let scoreB = 0;
    
    // Title matches get highest score
    if (a.title.toLowerCase().includes(queryLower)) scoreA += 10;
    if (b.title.toLowerCase().includes(queryLower)) scoreB += 10;
    
    // Author matches get medium score
    if (a.authors.some(author => author.toLowerCase().includes(queryLower))) scoreA += 5;
    if (b.authors.some(author => author.toLowerCase().includes(queryLower))) scoreB += 5;
    
    // Category matches get lower score
    if (a.categories?.some(cat => cat.toLowerCase().includes(queryLower))) scoreA += 2;
    if (b.categories?.some(cat => cat.toLowerCase().includes(queryLower))) scoreB += 2;
    
    // Higher rated books get slight preference
    scoreA += (a.averageRating || 0) * 0.1;
    scoreB += (b.averageRating || 0) * 0.1;
    
    return scoreB - scoreA;
  });
};

// User preference helpers
export const getUserFavoriteGenres = (userRatings, allBooks, minRating = 4) => {
  const genreCount = {};
  
  Object.entries(userRatings).forEach(([bookId, rating]) => {
    if (rating >= minRating) {
      const book = allBooks.find(b => b.id === bookId);
      if (book?.categories) {
        book.categories.forEach(category => {
          const normalizedGenre = category.toLowerCase();
          genreCount[normalizedGenre] = (genreCount[normalizedGenre] || 0) + 1;
        });
      }
    }
  });
  
  return Object.entries(genreCount)
    .sort(([,a], [,b]) => b - a)
    .map(([genre]) => genre)
    .slice(0, 5);
};

export const getUserFavoriteAuthors = (userRatings, allBooks, minRating = 4) => {
  const authorCount = {};
  
  Object.entries(userRatings).forEach(([bookId, rating]) => {
    if (rating >= minRating) {
      const book = allBooks.find(b => b.id === bookId);
      if (book?.authors) {
        book.authors.forEach(author => {
          authorCount[author] = (authorCount[author] || 0) + 1;
        });
      }
    }
  });
  
  return Object.entries(authorCount)
    .sort(([,a], [,b]) => b - a)
    .map(([author]) => author)
    .slice(0, 5);
};

// Reading progress helpers
export const calculateReadingProgress = (readingList, readingGoal = 12) => {
  const finishedBooks = readingList.filter(book => 
    book.readingStatus === 'finished'
  ).length;
  
  const currentlyReading = readingList.filter(book => 
    book.readingStatus === 'currently-reading'
  ).length;
  
  const wantToRead = readingList.filter(book => 
    book.readingStatus === 'want-to-read' || !book.readingStatus
  ).length;
  
  return {
    finished: finishedBooks,
    currentlyReading,
    wantToRead,
    totalInList: readingList.length,
    progressToGoal: Math.min(100, Math.round((finishedBooks / readingGoal) * 100)),
    isOnTrack: finishedBooks >= readingGoal * (new Date().getMonth() + 1) / 12
  };
};

// Search and filter helpers
export const filterBooksByGenre = (books, genres) => {
  if (!genres || genres.length === 0) return books;
  
  return books.filter(book => 
    book.categories?.some(category =>
      genres.some(genre => 
        category.toLowerCase().includes(genre.toLowerCase())
      )
    )
  );
};

export const filterBooksByAuthor = (books, authors) => {
  if (!authors || authors.length === 0) return books;
  
  return books.filter(book =>
    book.authors?.some(bookAuthor =>
      authors.some(author =>
        bookAuthor.toLowerCase().includes(author.toLowerCase())
      )
    )
  );
};

export const filterBooksByRating = (books, minRating = 0) => {
  return books.filter(book => 
    (book.averageRating || 0) >= minRating
  );
};

export const filterBooksByYear = (books, startYear, endYear) => {
  return books.filter(book => {
    if (!book.publishedDate) return false;
    const year = parseInt(book.publishedDate.substring(0, 4));
    return year >= startYear && year <= endYear;
  });
};
