import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useBooks } from '../../contexts/BookContext';
import './Header.css';

const Header = () => {
  const { user, isAuthenticated, signOut } = useAuth();
  const { getBookStats } = useBooks();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const location = useLocation();

  if (!isAuthenticated) {
    return null;
  }

  const stats = getBookStats();

  const isActivePath = (path) => {
    return location.pathname === path;
  };

  const handleSignOut = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setShowUserMenu(false);
    signOut();
  };

  const handleMenuClick = (e) => {
    e.stopPropagation();
    setShowUserMenu(!showUserMenu);
  };

  const handleOverlayClick = () => {
    setShowUserMenu(false);
  };

  return (
    <>
      <header className="app-header">
        <nav className="main-nav">
          <Link to="/" className="logo-link">
            <div className="logo">
              <span className="logo-icon">üìö</span>
              <span className="logo-text">BookRecommender</span>
            </div>
          </Link>

          <div className="nav-links">
            <Link 
              to="/" 
              className={`nav-link ${isActivePath('/') ? 'active' : ''}`}
            >
              üè† Home
            </Link>
            <Link 
              to="/recommendations" 
              className={`nav-link ${isActivePath('/recommendations') ? 'active' : ''}`}
            >
              ‚ú® Recommendations
            </Link>
            <Link 
              to="/profile" 
              className={`nav-link ${isActivePath('/profile') ? 'active' : ''}`}
            >
              üë§ Profile
            </Link>
          </div>

          <div className="user-section">
            <div className="quick-stats">
              <span className="stat-item">üìö {stats.totalBooks}</span>
              <span className="stat-item">‚≠ê {stats.totalRated}</span>
            </div>

            <div className="user-menu" onClick={handleMenuClick}>
              <img 
                src={user?.picture || '/default-avatar.png'} 
                alt={user?.name || 'User'}
                className="user-avatar"
              />
              <span className="user-name">{user?.given_name || 'User'}</span>
              <span className="menu-arrow">‚ñº</span>
            </div>

            {showUserMenu && (
              <div className="user-dropdown">
                <div className="dropdown-header">
                  <img src={user?.picture} alt={user?.name} className="dropdown-avatar" />
                  <div className="dropdown-user-info">
                    <div className="dropdown-name">{user?.name}</div>
                    <div className="dropdown-email">{user?.email}</div>
                  </div>
                </div>
                <div className="dropdown-divider"></div>
                <Link to="/profile" className="dropdown-item" onClick={() => setShowUserMenu(false)}>
                  üë§ Profile Settings
                </Link>
                <div className="dropdown-divider"></div>
                <button onClick={handleSignOut} className="dropdown-item logout-item">
                  üö™ Sign Out
                </button>
              </div>
            )}
          </div>
        </nav>
      </header>

      {showUserMenu && <div className="menu-overlay" onClick={handleOverlayClick}></div>}
    </>
  );
};

export default Header;
