import React, { useState, useEffect } from 'react';
import { useBooks } from '../../contexts/BookContext';
import SearchBar from '../../components/SearchBar';
import { getPopularBooks } from '../../services/bookAPI';
import './Home.css';

const Home = () => {
  const { searchResults, loading, setLoading, setError } = useBooks();
  const [popularBooks, setPopularBooks] = useState([]);

  useEffect(() => {
    const loadPopularBooks = async () => {
      try {
        setLoading(true);
        const books = await getPopularBooks(12);
        setPopularBooks(books);
      } catch (error) {
        setError('Failed to load popular books');
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    loadPopularBooks();
  }, [setLoading, setError]);

  const handleAddToReadingList = (book) => {
    console.log('Adding to reading list:', book.title);
    // We'll implement this functionality next
  };

  const BookCard = ({ book, onAddToList }) => (
    <div className="book-card">
      <div className="book-image">
        {book.thumbnail ? (
          <img src={book.thumbnail} alt={book.title} />
        ) : (
          <div className="no-image">üìö</div>
        )}
      </div>
      <div className="book-info">
        <h4>{book.title}</h4>
        <p className="author">by {book.authors.join(', ')}</p>
        {book.averageRating > 0 && (
          <p className="rating">‚≠ê {book.averageRating.toFixed(1)}</p>
        )}
        <button 
          className="add-button"
          onClick={() => onAddToList(book)}
        >
          + Add to Reading List
        </button>
      </div>
    </div>
  );

  return (
    <div className="home">
      <section className="hero">
        <h2>Discover Your Next Great Read</h2>
        <p>Search for books or explore our recommendations</p>
        <SearchBar />
      </section>
      
      {searchResults.length > 0 && (
        <section className="search-results">
          <h3>Search Results ({searchResults.length} found)</h3>
          <div className="books-grid">
            {searchResults.map((book) => (
              <BookCard 
                key={book.id} 
                book={book} 
                onAddToList={handleAddToReadingList}
              />
            ))}
          </div>
        </section>
      )}
      
      <section className="popular-books">
        <h3>Popular Books</h3>
        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
            <p>Loading amazing books...</p>
          </div>
        ) : popularBooks.length > 0 ? (
          <div className="books-grid">
            {popularBooks.slice(0, 8).map((book) => (
              <BookCard 
                key={book.id} 
                book={book} 
                onAddToList={handleAddToReadingList}
              />
            ))}
          </div>
        ) : (
          <p>No books available</p>
        )}
      </section>
    </div>
  );
};

export default Home;
