import React, { useEffect, useRef } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import './GoogleSignIn.css';

const GoogleSignIn = () => {
  const { user, loading } = useAuth();
  const googleButtonRef = useRef(null);

  useEffect(() => {
    if (!user && !loading && window.google && googleButtonRef.current) {
      try {
        window.google.accounts.id.renderButton(
          googleButtonRef.current,
          {
            theme: 'filled_black',
            size: 'large',
            shape: 'rectangular',
            width: 300,
            text: 'signin_with',
            logo_alignment: 'left'
          }
        );
      } catch (error) {
        console.error('Error rendering Google Sign-In button:', error);
      }
    }
  }, [user, loading]);

  if (user || loading) {
    return null;
  }

  return (
    <div className="google-signin">
      <div className="signin-container">
        <h3>Sign In to Get Started</h3>
        <p>Sign in with Google to access personalized book recommendations, create reading lists, and track your reading progress.</p>
        <div ref={googleButtonRef} className="google-signin-button"></div>
        <div className="signin-features">
          <div className="feature">üìö Personalized recommendations</div>
          <div className="feature">üìñ Reading list management</div>
          <div className="feature">‚≠ê Book ratings and reviews</div>
          <div className="feature">üìä Reading statistics</div>
        </div>
      </div>
    </div>
  );
};

export default GoogleSignIn;
