import React, { useState } from 'react';
import { useUser } from '../../contexts/UserContext';
import { useBooks } from '../../contexts/BookContext';
import BookCard from '../../components/BookCard';
import './Profile.css';

const Profile = () => {
  const { user, preferences, updateUser, updatePreferences } = useUser();
  const { readingList, ratings } = useBooks();
  const [activeTab, setActiveTab] = useState('profile');
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user.name || '',
    email: user.email || '',
    readingGoal: preferences.readingGoal || 12
  });

  const handleSave = () => {
    updateUser({
      name: formData.name,
      email: formData.email,
      isSetup: true
    });
    updatePreferences({
      readingGoal: formData.readingGoal
    });
    setIsEditing(false);
  };

  const ratedBooks = readingList.filter(book => ratings[book.id]);
  const unratedBooks = readingList.filter(book => !ratings[book.id]);

  return (
    <div className="profile-page">
      <div className="profile-header">
        <h2>üë§ Your Profile</h2>
        <div className="tab-navigation">
          <button 
            className={activeTab === 'profile' ? 'active' : ''}
            onClick={() => setActiveTab('profile')}
          >
            Profile
          </button>
          <button 
            className={activeTab === 'reading-list' ? 'active' : ''}
            onClick={() => setActiveTab('reading-list')}
          >
            Reading List ({readingList.length})
          </button>
          <button 
            className={activeTab === 'ratings' ? 'active' : ''}
            onClick={() => setActiveTab('ratings')}
          >
            My Ratings ({ratedBooks.length})
          </button>
        </div>
      </div>

      {activeTab === 'profile' && (
        <div className="profile-content">
          <div className="profile-card">
            <div className="card-header">
              <h3>Personal Information</h3>
              <button 
                className="edit-button"
                onClick={() => isEditing ? handleSave() : setIsEditing(true)}
              >
                {isEditing ? 'Save' : 'Edit'}
              </button>
            </div>
            
            {isEditing ? (
              <div className="form-group">
                <label>
                  Name:
                  <input
                    type="text"
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    placeholder="Your name"
                  />
                </label>
                <label>
                  Email:
                  <input
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData({...formData, email: e.target.value})}
                    placeholder="your.email@example.com"
                  />
                </label>
                <label>
                  Reading Goal (books per year):
                  <input
                    type="number"
                    value={formData.readingGoal}
                    onChange={(e) => setFormData({...formData, readingGoal: parseInt(e.target.value) || 12})}
                    min="1"
                    max="365"
                  />
                </label>
              </div>
            ) : (
              <div className="info-display">
                <p><strong>Name:</strong> {user.name || 'Not set'}</p>
                <p><strong>Email:</strong> {user.email || 'Not set'}</p>
                <p><strong>Reading Goal:</strong> {preferences.readingGoal} books per year</p>
              </div>
            )}
          </div>

          <div className="stats-grid">
            <div className="stat-card">
              <h4>üìö Books in Reading List</h4>
              <span className="stat-number">{readingList.length}</span>
            </div>
            <div className="stat-card">
              <h4>‚≠ê Books Rated</h4>
              <span className="stat-number">{ratedBooks.length}</span>
            </div>
            <div className="stat-card">
              <h4>üéØ Progress to Goal</h4>
              <span className="stat-number">
                {Math.round((ratedBooks.length / preferences.readingGoal) * 100)}%
              </span>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'reading-list' && (
        <div className="reading-list-content">
          <h3>üìñ Your Reading List</h3>
          {readingList.length > 0 ? (
            <div className="books-grid">
              {readingList.map((book) => (
                <BookCard key={book.id} book={book} showAddToList={false} />
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <h4>üìö No books in your reading list yet</h4>
              <p>Start adding books from the home page to build your collection!</p>
            </div>
          )}
        </div>
      )}

      {activeTab === 'ratings' && (
        <div className="ratings-content">
          <h3>‚≠ê Your Rated Books</h3>
          {ratedBooks.length > 0 ? (
            <div className="books-grid">
              {ratedBooks.map((book) => (
                <BookCard key={book.id} book={book} showAddToList={false} />
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <h4>‚≠ê No rated books yet</h4>
              <p>Rate books in your reading list to see them here!</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Profile;
