import React from 'react';
import { useUser } from '../../contexts/UserContext';
import './Profile.css';

const Profile = () => {
  const { user, preferences } = useUser();

  return (
    <div className="profile-page">
      <h2>Your Profile</h2>
      <div className="profile-content">
        <div className="user-info">
          <h3>User Information</h3>
          <p><strong>Name:</strong> {user.name || 'Not set'}</p>
          <p><strong>Email:</strong> {user.email || 'Not set'}</p>
        </div>
        
        <div className="preferences">
          <h3>Reading Preferences</h3>
          <p><strong>Favorite Genres:</strong> {preferences.genres.length > 0 ? preferences.genres.join(', ') : 'None selected'}</p>
          <p><strong>Favorite Authors:</strong> {preferences.authors.length > 0 ? preferences.authors.join(', ') : 'None selected'}</p>
          <p><strong>Reading Goal:</strong> {preferences.readingGoal} books per year</p>
        </div>
        
        <div className="setup-notice">
          <p>Profile setup coming soon! You'll be able to customize your preferences here.</p>
        </div>
      </div>
    </div>
  );
};

export default Profile;
