import React, { useState, useEffect } from 'react';
import { useBooks } from '../../contexts/BookContext';
import { useUser } from '../../contexts/UserContext';
import BookCard from '../../components/BookCard';
import { getRecommendedBooks, searchBooks, getPopularBooks } from '../../services/bookAPI';
import { 
  generateRecommendations, 
  getRecommendationReason, 
  formatBookForRecommendation,
  getUserFavoriteGenres,
  getUserFavoriteAuthors
} from '../../utils/helpers';
import { 
  Sparkles, 
  TrendingUp, 
  RefreshCw, 
  BookOpen, 
  Star, 
  Target,
  AlertCircle,
  ChevronRight
} from 'lucide-react';
import './Recommendations.css';

const Recommendations = () => {
  const { readingList, ratings, getBookStats } = useBooks();
  const { preferences } = useUser();
  const [personalizedRecs, setPersonalizedRecs] = useState([]);
  const [trendingBooks, setTrendingBooks] = useState([]);
  const [allBooks, setAllBooks] = useState([]);
  const [activeTab, setActiveTab] = useState('for-you');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const stats = getBookStats();
  const hasPreferences = preferences.genres?.length > 0 || preferences.authors?.length > 0;
  const hasRatings = Object.keys(ratings).length > 0;
  const hasBooks = readingList.length > 0;

  useEffect(() => {
    loadTrendingBooks();
    loadAllBooksForRecommendations();
    if (hasPreferences || hasRatings) {
      loadPersonalizedRecommendations();
    }
  }, [hasPreferences, hasRatings]);

  const loadAllBooksForRecommendations = async () => {
    // Combine books from reading list and any cached books
    const books = [...readingList];
    setAllBooks(books);
  };

  const loadTrendingBooks = async () => {
    try {
      setLoading(true);
      const trending = await getPopularBooks(12);
      setTrendingBooks(trending);
    } catch (error) {
      console.error('Error loading trending books:', error);
      setError('Failed to load trending books');
    } finally {
      setLoading(false);
    }
  };

  const loadPersonalizedRecommendations = async () => {
    try {
      setLoading(true);
      setError(null);
      
      let recommendations = [];

      // Use the advanced recommendation algorithm if we have ratings
      if (hasRatings && allBooks.length > 0) {
        const smartRecs = generateRecommendations(ratings, preferences, allBooks, readingList);
        recommendations = smartRecs.map(book => {
          const reason = getRecommendationReason(book, ratings, preferences, allBooks);
          return formatBookForRecommendation(book, reason);
        });
      }

      // Fallback to preference-based recommendations
      if (recommendations.length < 8 && preferences.genres?.length > 0) {
        for (const genre of preferences.genres.slice(0, 3)) {
          try {
            const genreBooks = await searchBooks(`subject:${genre}`, 4);
            const filteredBooks = genreBooks.filter(book => 
              !readingList.some(rb => rb.id === book.id) &&
              !recommendations.some(rb => rb.id === book.id)
            );
            
            filteredBooks.forEach(book => {
              book.recommendationReason = `Because you like ${genre}`;
            });
            
            recommendations = [...recommendations, ...filteredBooks.slice(0, 2)];
          } catch (error) {
            console.error(`Error loading ${genre} books:`, error);
          }
        }
      }

      // Author-based recommendations
      if (recommendations.length < 8 && preferences.authors?.length > 0) {
        for (const author of preferences.authors.slice(0, 2)) {
          try {
            const authorBooks = await searchBooks(`inauthor:"${author}"`, 4);
            const filteredBooks = authorBooks.filter(book => 
              !readingList.some(rb => rb.id === book.id) &&
              !recommendations.some(rb => rb.id === book.id)
            );
            
            filteredBooks.forEach(book => {
              book.recommendationReason = `More books by ${author}`;
            });
            
            recommendations = [...recommendations, ...filteredBooks.slice(0, 2)];
          } catch (error) {
            console.error(`Error loading books by ${author}:`, error);
          }
        }
      }

      // Remove duplicates and limit results
      const uniqueRecommendations = recommendations
        .filter((book, index, self) => self.findIndex(b => b.id === book.id) === index)
        .slice(0, 12);

      setPersonalizedRecs(uniqueRecommendations);
      
    } catch (error) {
      console.error('Error loading personalized recommendations:', error);
      setError('Failed to load personalized recommendations');
    } finally {
      setLoading(false);
    }
  };

  const handleRefreshRecommendations = () => {
    if (hasPreferences || hasRatings) {
      loadPersonalizedRecommendations();
    }
    loadTrendingBooks();
  };

  const renderEmptyState = () => {
    const favoriteGenres = hasRatings ? getUserFavoriteGenres(ratings, allBooks) : [];
    const favoriteAuthors = hasRatings ? getUserFavoriteAuthors(ratings, allBooks) : [];

    return (
      <div className="recommendation-empty-state">
        <AlertCircle size={64} />
        <h3>Building your recommendations...</h3>
        <p>We need a bit more information to create perfect recommendations for you!</p>
        
        {favoriteGenres.length > 0 && (
          <div className="discovered-preferences">
            <h4>We noticed you like:</h4>
            <div className="preference-tags">
              {favoriteGenres.map(genre => (
                <span key={genre} className="preference-tag">{genre}</span>
              ))}
            </div>
          </div>
        )}
        
        <div className="recommendation-tips">
          <h4>Help us recommend better books:</h4>
          
          <div className="tip-item">
            <div className="tip-icon">
              <Star size={24} />
            </div>
            <div className="tip-content">
              <h5>Rate some books</h5>
              <p>Rate books in your library to help us understand your taste</p>
            </div>
          </div>

          <div className="tip-item">
            <div className="tip-icon">
              <Target size={24} />
            </div>
            <div className="tip-content">
              <h5>Add favorite genres</h5>
              <p>Update your preferences to get genre-based recommendations</p>
            </div>
          </div>

          <div className="tip-item">
            <div className="tip-icon">
              <BookOpen size={24} />
            </div>
            <div className="tip-content">
              <h5>Add more books</h5>
              <p>Build your reading list from our book collection</p>
            </div>
          </div>
        </div>

        <button 
          className="setup-preferences-button"
          onClick={() => window.location.href = '/profile'}
        >
          Set up preferences <ChevronRight size={16} />
        </button>
      </div>
    );
  };

  return (
    <div className="recommendations-page">
      <div className="recommendations-header">
        <h2>
          <BookOpen size={32} />
          Your Book Recommendations
        </h2>
        
        <div className="recommendation-stats">
          <span className="stat">
            <BookOpen size={16} />
            {stats.totalBooks} books in library
          </span>
          <span className="stat">
            <Star size={16} />
            {stats.totalRated} books rated
          </span>
          <span className="stat">
            <Target size={16} />
            {preferences.genres?.length || 0} favorite genres
          </span>
        </div>
      </div>

      <div className="recommendations-tabs">
        <button 
          className={`tab-button ${activeTab === 'for-you' ? 'active' : ''}`}
          onClick={() => setActiveTab('for-you')}
        >
          For You ({personalizedRecs.length})
        </button>
        <button 
          className={`tab-button ${activeTab === 'trending' ? 'active' : ''}`}
          onClick={() => setActiveTab('trending')}
        >
          Trending ({trendingBooks.length})
        </button>
      </div>

      {activeTab === 'for-you' && (
        <div className="tab-content">
          <div className="tab-header">
            <button 
              className="refresh-button"
              onClick={handleRefreshRecommendations}
              disabled={loading}
            >
              <RefreshCw size={16} className={loading ? 'spinning' : ''} />
              Refresh Recommendations
            </button>
          </div>

          {error && (
            <div className="error-message">
              <AlertCircle size={16} />
              {error}
            </div>
          )}

          {(!hasPreferences && !hasRatings) || personalizedRecs.length === 0 ? (
            renderEmptyState()
          ) : (
            <div className="recommendations-section">
              <h3>
                <Sparkles size={24} />
                Recommended for You
              </h3>
              <div className="books-grid">
                {personalizedRecs.map((book) => (
                  <BookCard key={book.id} book={book} />
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {activeTab === 'trending' && (
        <div className="tab-content">
          <div className="recommendations-section">
            <h3>
              <TrendingUp size={24} />
              Trending Books
            </h3>
            {loading ? (
              <div className="loading-state">
                <div className="spinner"></div>
                <p>Loading trending books...</p>
              </div>
            ) : (
              <div className="books-grid">
                {trendingBooks.map((book) => (
                  <BookCard key={book.id} book={book} />
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Recommendations;
