import React, { useState, useEffect } from 'react';
import { useBooks } from '../../contexts/BookContext';
import SearchBar from '../../components/SearchBar';
import RecommendationList from '../../components/RecommendationList';
import { getPopularBooks } from '../../services/bookAPI';
import './Home.css';

const Home = () => {
  const { searchResults, setSearchResults, loading, setLoading, setError } = useBooks();
  const [popularBooks, setPopularBooks] = useState([]);

  useEffect(() => {
    const loadPopularBooks = async () => {
      try {
        setLoading(true);
        const books = await getPopularBooks(12);
        setPopularBooks(books);
      } catch (error) {
        setError('Failed to load popular books');
      } finally {
        setLoading(false);
      }
    };

    loadPopularBooks();
  }, [setLoading, setError]);

  return (
    <div className="home">
      <section className="hero">
        <h2>Discover Your Next Great Read</h2>
        <p>Search for books or explore our recommendations</p>
        <SearchBar />
      </section>
      
      {searchResults.length > 0 && (
        <section className="search-results">
          <h3>Search Results</h3>
          <RecommendationList books={searchResults} showAddToList={true} />
        </section>
      )}
      
      <section className="popular-books">
        <h3>Popular Books</h3>
        {loading ? (
          <div className="loading">Loading books...</div>
        ) : (
          <RecommendationList books={popularBooks} showAddToList={true} />
        )}
      </section>
    </div>
  );
};

export default Home;
