import React from 'react';
import { useBooks } from '../../contexts/BookContext';
import { useUser } from '../../contexts/UserContext';
import './Recommendations.css';

const Recommendations = () => {
  const { recommendations } = useBooks();
  const { user } = useUser();

  return (
    <div className="recommendations-page">
      <h2>Your Personal Recommendations</h2>
      <div className="recommendations-content">
        {!user.isSetup ? (
          <div className="setup-prompt">
            <h3>Complete your profile to get personalized recommendations!</h3>
            <p>Visit your profile page to set up your reading preferences.</p>
          </div>
        ) : recommendations.length > 0 ? (
          <div className="recommendations-list">
            {recommendations.map((book) => (
              <div key={book.id} className="recommendation-item">
                <h4>{book.title}</h4>
                <p>by {book.authors.join(', ')}</p>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-recommendations">
            <h3>No recommendations yet</h3>
            <p>Recommendations will appear here based on your preferences and reading history.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Recommendations;
