import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleCredentialResponse = (response) => {
    try {
      // Decode JWT token to get user info
      const userInfo = parseJwt(response.credential);
      const userData = {
        id: userInfo.sub,
        email: userInfo.email,
        name: userInfo.name,
        picture: userInfo.picture,
        given_name: userInfo.given_name,
        family_name: userInfo.family_name,
        isSetup: true,
        loginTime: new Date().toISOString()
      };

      setUser(userData);
      setIsAuthenticated(true);
      setError(null);
      
      // Save to localStorage
      localStorage.setItem('bookApp_user', JSON.stringify(userData));
      localStorage.setItem('bookApp_isAuthenticated', 'true');

    } catch (error) {
      console.error('Error processing Google sign-in:', error);
      setError('Failed to sign in with Google');
    }
  };

  const parseJwt = (token) => {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
      return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));
    return JSON.parse(jsonPayload);
  };

  useEffect(() => {
    const initializeGoogleAuth = () => {
      if (window.google) {
        window.google.accounts.id.initialize({
          client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID,
          callback: handleCredentialResponse,
          auto_select: false,
          cancel_on_tap_outside: false,
        });
        
        // Check if user is already signed in
        const savedUser = localStorage.getItem('bookApp_user');
        const savedAuth = localStorage.getItem('bookApp_isAuthenticated');
        
        if (savedUser && savedAuth === 'true') {
          try {
            const userData = JSON.parse(savedUser);
            setUser(userData);
            setIsAuthenticated(true);
          } catch (error) {
            console.error('Error loading saved user:', error);
            localStorage.removeItem('bookApp_user');
            localStorage.removeItem('bookApp_isAuthenticated');
          }
        }
        setLoading(false);
      }
    };

    // Load Google Identity API
    if (!window.google) {
      const script = document.createElement('script');
      script.src = 'https://accounts.google.com/gsi/client';
      script.async = true;
      script.defer = true;
      script.onload = initializeGoogleAuth;
      document.head.appendChild(script);
    } else {
      initializeGoogleAuth();
    }
  }, []);

  const signInWithGoogle = () => {
    setError(null);
    if (window.google) {
      window.google.accounts.id.prompt();
    }
  };

  const signOut = () => {
    if (window.google) {
      window.google.accounts.id.disableAutoSelect();
    }
    
    setUser(null);
    setIsAuthenticated(false);
    setError(null);
    
    // Clear localStorage
    localStorage.removeItem('bookApp_user');
    localStorage.removeItem('bookApp_isAuthenticated');
    localStorage.removeItem('bookApp_preferences');
    localStorage.removeItem('bookApp_readingList');
    localStorage.removeItem('bookApp_ratings');
  };

  const updateUserProfile = (updates) => {
    if (user) {
      const updatedUser = { ...user, ...updates };
      setUser(updatedUser);
      localStorage.setItem('bookApp_user', JSON.stringify(updatedUser));
    }
  };

  const value = {
    user,
    isAuthenticated,
    loading,
    error,
    signInWithGoogle,
    signOut,
    updateUserProfile,
    setError
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
