import React, { useState } from 'react';
import { useBooks } from '../../contexts/BookContext';
import Rating from '../Rating';
import './BookModal.css';

const BookModal = ({ book, isOpen, onClose }) => {
  const { addToReadingList, removeFromReadingList, readingList, rateBook, ratings } = useBooks();
  const [userNotes, setUserNotes] = useState('');
  const [readingStatus, setReadingStatus] = useState('want-to-read');

  if (!isOpen || !book) return null;

  const isInReadingList = readingList.some(b => b.id === book.id);
  const userRating = ratings[book.id] || 0;

  const handleToggleReadingList = () => {
    if (isInReadingList) {
      removeFromReadingList(book.id);
    } else {
      addToReadingList({ ...book, readingStatus, notes: userNotes });
    }
  };

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'currently-reading': return '#28a745';
      case 'finished': return '#007bff';
      case 'want-to-read': return '#ffc107';
      default: return '#6c757d';
    }
  };

  const openGoogleBooks = () => {
    if (book.infoLink) {
      window.open(book.infoLink, '_blank');
    }
  };

  return (
    <div className="modal-backdrop" onClick={handleBackdropClick}>
      <div className="book-modal">
        <button className="close-button" onClick={onClose}>
          ✕
        </button>
        
        <div className="modal-content">
          <div className="modal-left">
            <div className="book-cover-large">
              {book.thumbnail ? (
                <img src={book.thumbnail.replace('zoom=1', 'zoom=2')} alt={book.title} />
              ) : (
                <div className="no-image-large">📚</div>
              )}
            </div>
            
            <div className="book-actions-modal">
              <div className="reading-status-selector">
                <label>Reading Status:</label>
                <select 
                  value={readingStatus} 
                  onChange={(e) => setReadingStatus(e.target.value)}
                  style={{ backgroundColor: getStatusColor(readingStatus) }}
                >
                  <option value="want-to-read">📚 Want to Read</option>
                  <option value="currently-reading">📖 Currently Reading</option>
                  <option value="finished">✅ Finished</option>
                </select>
              </div>
              
              <button 
                className={`modal-action-button ${isInReadingList ? 'remove' : 'add'}`}
                onClick={handleToggleReadingList}
              >
                {isInReadingList ? '✓ In Your Library' : '+ Add to Library'}
              </button>

              <div className="rating-section">
                <label>Your Rating:</label>
                <Rating 
                  bookId={book.id}
                  currentRating={userRating}
                  onRate={(rating) => rateBook(book.id, rating)}
                  size="large"
                />
              </div>
            </div>
          </div>

          <div className="modal-right">
            <div className="book-header">
              <h2>{book.title}</h2>
              <p className="book-authors">by {book.authors.join(', ')}</p>
              
              <div className="book-metadata-detailed">
                {book.publishedDate && (
                  <span className="metadata-item">
                    📅 Published: {new Date(book.publishedDate).getFullYear()}
                  </span>
                )}
                {book.pageCount > 0 && (
                  <span className="metadata-item">
                    📖 Pages: {book.pageCount}
                  </span>
                )}
                {book.averageRating > 0 && (
                  <span className="metadata-item">
                    ⭐ {book.averageRating.toFixed(1)} ({book.ratingsCount} ratings)
                  </span>
                )}
                {book.categories && book.categories.length > 0 && (
                  <div className="categories-detailed">
                    <strong>Categories:</strong>
                    <div className="category-tags-modal">
                      {book.categories.slice(0, 5).map((category, index) => (
                        <span key={index} className="category-tag-modal">
                          {category}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>

            <div className="book-description-detailed">
              <h3>Description</h3>
              <div className="description-content">
                {book.description ? (
                  <p dangerouslySetInnerHTML={{ 
                    __html: book.description.replace(/<[^>]*>/g, '') 
                  }} />
                ) : (
                  <p>No description available.</p>
                )}
              </div>
            </div>

            <div className="personal-notes">
              <h3>Your Notes</h3>
              <textarea
                value={userNotes}
                onChange={(e) => setUserNotes(e.target.value)}
                placeholder="Add your thoughts about this book..."
                rows={4}
              />
            </div>

            <div className="modal-footer">
              <button className="google-books-button" onClick={openGoogleBooks}>
                🔗 View on Google Books
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookModal;
