import React, { useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import './Login.css';

const Login = () => {
  const { loading, error } = useAuth();

  useEffect(() => {
    // Render the Google button when component mounts
    const renderGoogleButton = () => {
      if (window.google) {
        const buttonDiv = document.getElementById('google-signin-button');
        if (buttonDiv && !buttonDiv.hasChildNodes()) {
          window.google.accounts.id.renderButton(buttonDiv, {
            theme: 'filled_blue',
            size: 'large',
            text: 'signin_with',
            shape: 'rectangular',
            width: '100%'
          });
        }
      }
    };

    // Try to render immediately, or wait for Google API to load
    if (window.google) {
      setTimeout(renderGoogleButton, 100);
    } else {
      const checkForGoogle = setInterval(() => {
        if (window.google) {
          clearInterval(checkForGoogle);
          setTimeout(renderGoogleButton, 100);
        }
      }, 100);
      
      // Clean up interval after 10 seconds
      setTimeout(() => clearInterval(checkForGoogle), 10000);
    }
  }, []);

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="app-logo">üìö</div>
          <h1>BookRecommender</h1>
          <p>Discover your next great read with personalized recommendations</p>
        </div>

        <div className="login-content">
          <h2>Welcome!</h2>
          <p>Sign in to save your reading list, get personalized recommendations, and sync across devices.</p>

          {error && (
            <div className="error-message">
              <span>‚ö†Ô∏è {error}</span>
              <button onClick={() => window.location.reload()}>
                Try Again
              </button>
            </div>
          )}

          <div className="signin-section">
            {loading && <p>Loading authentication...</p>}
            <div id="google-signin-button" style={{
              display: 'flex',
              justifyContent: 'center',
              margin: '2rem 0'
            }}></div>
          </div>

          <div className="features-list">
            <h3>What you'll get:</h3>
            <ul>
              <li>üéØ Personalized book recommendations</li>
              <li>üìö Save and organize your reading list</li>
              <li>‚≠ê Rate and review books</li>
              <li>üìä Track your reading progress</li>
              <li>‚òÅÔ∏è Sync across all your devices</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
