import React, { useState, useEffect } from 'react';
import { useBooks } from '../../contexts/BookContext';
import { getPopularBooks } from '../../services/bookAPI';
import './Home.css';

const Home = () => {
  const { loading, setLoading, setError } = useBooks();
  const [popularBooks, setPopularBooks] = useState([]);

  useEffect(() => {
    const loadPopularBooks = async () => {
      try {
        setLoading(true);
        const books = await getPopularBooks(12);
        setPopularBooks(books);
      } catch (error) {
        setError('Failed to load popular books');
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    loadPopularBooks();
  }, [setLoading, setError]);

  return (
    <div className="home">
      <section className="hero">
        <h2>Discover Your Next Great Read</h2>
        <p>Search for books or explore our recommendations</p>
        {/* SearchBar component will be added later */}
        <div className="search-placeholder">
          <input type="text" placeholder="Search for books, authors, or genres..." />
          <button>Search</button>
        </div>
      </section>
      
      <section className="popular-books">
        <h3>Popular Books</h3>
        {loading ? (
          <div className="loading">Loading books...</div>
        ) : popularBooks.length > 0 ? (
          <div className="books-grid">
            {popularBooks.slice(0, 6).map((book) => (
              <div key={book.id} className="book-card-simple">
                <h4>{book.title}</h4>
                <p>by {book.authors.join(', ')}</p>
                {book.thumbnail && (
                  <img src={book.thumbnail} alt={book.title} />
                )}
              </div>
            ))}
          </div>
        ) : (
          <p>No books available</p>
        )}
      </section>
    </div>
  );
};

export default Home;
