import React, { useState } from 'react';
import { useBooks } from '../../contexts/BookContext';
import Rating from '../Rating';
import BookModal from '../BookModal';
import ReadingProgress from '../ReadingProgress';
import './BookCard.css';

const BookCard = ({ book, showAddToList = true, showRating = true, showProgress = false }) => {
  const { addToReadingList, removeFromReadingList, readingList, rateBook, ratings, updateBookProgress } = useBooks();
  const [isModalOpen, setIsModalOpen] = useState(false);
  
  const isInReadingList = readingList.some(b => b.id === book.id);
  const userRating = ratings[book.id] || 0;
  const bookInList = readingList.find(b => b.id === book.id);

  const handleToggleReadingList = (e) => {
    e.stopPropagation(); // Prevent modal from opening
    if (isInReadingList) {
      removeFromReadingList(book.id);
    } else {
      addToReadingList(book);
    }
  };

  const handleRate = (rating) => {
    rateBook(book.id, rating);
  };

  const handleUpdateProgress = (progressData) => {
    updateBookProgress(book.id, progressData);
  };

  const truncateText = (text, maxLength = 150) => {
    if (!text) return 'No description available';
    const cleanText = text.replace(/<[^>]*>/g, ''); // Remove HTML tags
    return cleanText.length > maxLength ? cleanText.substring(0, maxLength) + '...' : cleanText;
  };

  const getReadingStatusBadge = () => {
    if (!bookInList) return null;
    
    const status = bookInList.readingStatus || 'want-to-read';
    const statusConfig = {
      'want-to-read': { label: 'Want to Read', color: '#ffc107', emoji: '📚' },
      'currently-reading': { label: 'Reading', color: '#28a745', emoji: '📖' },
      'finished': { label: 'Finished', color: '#007bff', emoji: '✅' }
    };

    const config = statusConfig[status];
    return (
      <div 
        className="reading-status-badge"
        style={{ backgroundColor: config.color }}
      >
        {config.emoji} {config.label}
      </div>
    );
  };

  return (
    <>
      <div className="book-card" onClick={() => setIsModalOpen(true)}>
        <div className="book-image">
          {book.thumbnail ? (
            <img src={book.thumbnail} alt={book.title} />
          ) : (
            <div className="no-image">📚</div>
          )}
          {getReadingStatusBadge()}
        </div>
        
        <div className="book-info">
          <h4 className="book-title">{book.title}</h4>
          <p className="book-author">by {book.authors.join(', ')}</p>
          
          <div className="book-metadata">
            {book.publishedDate && (
              <span className="publish-date">📅 {book.publishedDate.substring(0, 4)}</span>
            )}
            {book.pageCount > 0 && (
              <span className="page-count">📖 {book.pageCount} pages</span>
            )}
            {book.averageRating > 0 && (
              <span className="avg-rating">⭐ {book.averageRating.toFixed(1)}</span>
            )}
          </div>

          <p className="book-description">
            {truncateText(book.description)}
          </p>

          {book.categories && book.categories.length > 0 && (
            <div className="book-categories">
              {book.categories.slice(0, 2).map((category, index) => (
                <span key={index} className="category-tag">
                  {category}
                </span>
              ))}
            </div>
          )}

          {/* Show progress if book is currently being read */}
          {bookInList && bookInList.readingStatus === 'currently-reading' && showProgress && (
            <ReadingProgress 
              book={bookInList}
              onUpdateProgress={handleUpdateProgress}
            />
          )}

          <div className="book-actions">
            {showRating && (
              <Rating 
                bookId={book.id}
                currentRating={userRating}
                onRate={handleRate}
              />
            )}
            
            {showAddToList && (
              <button 
                className={`action-button ${isInReadingList ? 'remove' : 'add'}`}
                onClick={handleToggleReadingList}
              >
                {isInReadingList ? '✓ In Reading List' : '+ Add to Reading List'}
              </button>
            )}

            <button 
              className="view-details-button"
              onClick={(e) => {
                e.stopPropagation();
                setIsModalOpen(true);
              }}
            >
              👁️ View Details
            </button>
          </div>
        </div>
      </div>

      <BookModal 
        book={book}
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
      />
    </>
  );
};

export default BookCard;
