import React, { useState } from 'react';
import { useUser } from '../../contexts/UserContext';
import { useRecommendations } from '../../hooks/useRecommendations';
import BookCard from '../../components/BookCard';
import PreferenceSelector from '../../components/UserProfile';
import './Recommendations.css';

const Recommendations = () => {
  const { user, preferences } = useUser();
  const { recommendations, trendingBooks, loading, error, refreshRecommendations } = useRecommendations();
  const [activeTab, setActiveTab] = useState('for-you');

  const RecommendationCard = ({ book }) => (
    <div className="recommendation-card-wrapper">
      <BookCard book={book} />
      <div className="recommendation-reason">
        <span className="reason-icon">💡</span>
        <span className="reason-text">{book.recommendationReason}</span>
      </div>
    </div>
  );

  const hasPreferences = preferences.genres.length > 0 || preferences.authors.length > 0;
  const hasRatings = Object.keys(useRecommendations().recommendations).length > 0;

  if (!hasPreferences) {
    return (
      <div className="recommendations-page">
        <div className="setup-recommendations">
          <h2>🎯 Get Personalized Recommendations</h2>
          <div className="setup-card">
            <div className="setup-content">
              <h3>Tell us what you like!</h3>
              <p>Set up your reading preferences to get book recommendations tailored just for you.</p>
              <PreferenceSelector />
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="recommendations-page">
      <div className="recommendations-header">
        <h2>📖 Your Book Recommendations</h2>
        <div className="tab-navigation">
          <button 
            className={activeTab === 'for-you' ? 'active' : ''}
            onClick={() => setActiveTab('for-you')}
          >
            For You ({recommendations.length})
          </button>
          <button 
            className={activeTab === 'trending' ? 'active' : ''}
            onClick={() => setActiveTab('trending')}
          >
            Trending ({trendingBooks.length})
          </button>
        </div>
        <button className="refresh-button" onClick={refreshRecommendations}>
          🔄 Refresh Recommendations
        </button>
      </div>

      {error && (
        <div className="error-message">
          <p>⚠️ {error}</p>
          <button onClick={() => refreshRecommendations()}>Try Again</button>
        </div>
      )}

      {activeTab === 'for-you' && (
        <div className="recommendations-section">
          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
              <p>Generating personalized recommendations...</p>
            </div>
          ) : recommendations.length > 0 ? (
            <>
              <div className="section-info">
                <h3>✨ Curated Just for You</h3>
                <p>Based on your ratings, preferences, and reading history</p>
              </div>
              <div className="recommendations-grid">
                {recommendations.map((book) => (
                  <RecommendationCard key={book.id} book={book} />
                ))}
              </div>
            </>
          ) : (
            <div className="no-recommendations">
              <h3>🤔 No personalized recommendations yet</h3>
              <p>Rate some books or update your preferences to get better recommendations!</p>
              <div className="suggestions">
                <h4>Try these actions:</h4>
                <ul>
                  <li>⭐ Rate books in your reading list</li>
                  <li>🎯 Update your favorite genres in your profile</li>
                  <li>📚 Add more books to your reading list</li>
                </ul>
              </div>
            </div>
          )}
        </div>
      )}

      {activeTab === 'trending' && (
        <div className="trending-section">
          <div className="section-info">
            <h3>🔥 Trending Books</h3>
            <p>Popular books that readers are loving right now</p>
          </div>
          {trendingBooks.length > 0 ? (
            <div className="books-grid">
              {trendingBooks.map((book) => (
                <BookCard key={book.id} book={book} />
              ))}
            </div>
          ) : (
            <div className="loading">
              <div className="spinner"></div>
              <p>Loading trending books...</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Recommendations;
