const BASE_URL = 'https://www.googleapis.com/books/v1/volumes';
const API_KEY = ''; // Add your Google Books API key here (optional)

export const searchBooks = async (query, maxResults = 20) => {
  try {
    const response = await fetch(
      `${BASE_URL}?q=${encodeURIComponent(query)}&maxResults=${maxResults}${
        API_KEY ? `&key=${API_KEY}` : ''
      }`
    );
    const data = await response.json();
    return data.items || [];
  } catch (error) {
    console.error('Error searching books:', error);
    return [];
  }
};

export const getBooksByGenre = async (genre, maxResults = 20) => {
  return searchBooks(`subject:${genre}`, maxResults);
};

export const getBooksByAuthor = async (author, maxResults = 20) => {
  return searchBooks(`inauthor:${author}`, maxResults);
};

export const getPopularBooks = async (maxResults = 20) => {
  return searchBooks('bestseller', maxResults);
};
