import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleCredentialResponse = (response) => {
    try {
      console.log('Google response:', response); // Debug log
      
      // Decode JWT token to get user info
      const userInfo = parseJwt(response.credential);
      console.log('Parsed user info:', userInfo); // Debug log
      
      const userData = {
        id: userInfo.sub,
        email: userInfo.email,
        name: userInfo.name,
        picture: userInfo.picture,
        given_name: userInfo.given_name,
        family_name: userInfo.family_name,
        isSetup: true,
        loginTime: new Date().toISOString()
      };

      setUser(userData);
      setIsAuthenticated(true);
      setError(null);
      
      // Save to localStorage
      localStorage.setItem('bookApp_user', JSON.stringify(userData));
      localStorage.setItem('bookApp_isAuthenticated', 'true');

      console.log('User signed in successfully:', userData); // Debug log

    } catch (error) {
      console.error('Error processing Google sign-in:', error);
      setError('Failed to process Google sign-in: ' + error.message);
    }
  };

  const parseJwt = (token) => {
    try {
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));
      return JSON.parse(jsonPayload);
    } catch (error) {
      console.error('Error parsing JWT:', error);
      throw new Error('Invalid JWT token');
    }
  };

  useEffect(() => {
    const initializeGoogleAuth = () => {
      console.log('Initializing Google Auth...'); // Debug log
      console.log('Client ID:', process.env.REACT_APP_GOOGLE_CLIENT_ID); // Debug log
      
      if (window.google && process.env.REACT_APP_GOOGLE_CLIENT_ID) {
        try {
          window.google.accounts.id.initialize({
            client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID,
            callback: handleCredentialResponse,
            auto_select: false,
            cancel_on_tap_outside: false,
          });
          
          console.log('Google Auth initialized successfully'); // Debug log
          
          // Check if user is already signed in
          const savedUser = localStorage.getItem('bookApp_user');
          const savedAuth = localStorage.getItem('bookApp_isAuthenticated');
          
          if (savedUser && savedAuth === 'true') {
            try {
              const userData = JSON.parse(savedUser);
              setUser(userData);
              setIsAuthenticated(true);
              console.log('Restored user session:', userData); // Debug log
            } catch (error) {
              console.error('Error loading saved user:', error);
              localStorage.removeItem('bookApp_user');
              localStorage.removeItem('bookApp_isAuthenticated');
            }
          }
        } catch (error) {
          console.error('Error initializing Google Auth:', error);
          setError('Failed to initialize Google authentication');
        }
      } else {
        console.error('Google API not loaded or Client ID missing');
        setError('Google authentication not available');
      }
      setLoading(false);
    };

    // Load Google Identity API
    if (!window.google) {
      console.log('Loading Google API script...'); // Debug log
      const script = document.createElement('script');
      script.src = 'https://accounts.google.com/gsi/client';
      script.async = true;
      script.defer = true;
      script.onload = () => {
        console.log('Google API script loaded'); // Debug log
        setTimeout(initializeGoogleAuth, 100); // Small delay to ensure API is ready
      };
      script.onerror = () => {
        console.error('Failed to load Google API script');
        setError('Failed to load Google authentication');
        setLoading(false);
      };
      document.head.appendChild(script);
    } else {
      initializeGoogleAuth();
    }
  }, []);

const signInWithGoogle = () => {
  console.log('Sign in button clicked'); // Debug log
  setError(null);
  
  if (window.google) {
    try {
      // Just render the Google button if it's not already there
      const buttonDiv = document.getElementById('google-signin-button');
      if (buttonDiv && !buttonDiv.hasChildNodes()) {
        window.google.accounts.id.renderButton(buttonDiv, {
          theme: 'filled_blue',
          size: 'large',
          text: 'signin_with',
          shape: 'rectangular',
          width: '300'
        });
      }
    } catch (error) {
      console.error('Error during sign in:', error);
      setError('Sign in failed: ' + error.message);
    }
  } else {
    setError('Google authentication not available');
  }
};

  const signOut = () => {
    console.log('Signing out...'); // Debug log
    if (window.google) {
      window.google.accounts.id.disableAutoSelect();
    }
    
    setUser(null);
    setIsAuthenticated(false);
    setError(null);
    
    // Clear localStorage
    localStorage.removeItem('bookApp_user');
    localStorage.removeItem('bookApp_isAuthenticated');
    localStorage.removeItem('bookApp_preferences');
    localStorage.removeItem('bookApp_readingList');
    localStorage.removeItem('bookApp_ratings');
    
    console.log('User signed out successfully'); // Debug log
  };

  const updateUserProfile = (updates) => {
    if (user) {
      const updatedUser = { ...user, ...updates };
      setUser(updatedUser);
      localStorage.setItem('bookApp_user', JSON.stringify(updatedUser));
    }
  };

  const value = {
    user,
    isAuthenticated,
    loading,
    error,
    signInWithGoogle,
    signOut,
    updateUserProfile,
    setError
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
